package Chapter3;

public class FlowTest2 {
	/**
	 * if-else문 테스트
	 * @param args
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 30;
		if(score >= 80) {
			System.out.println("당신은 우등생입니다.");
			
		}
		else {
			System.out.println("당신은 우등생은 아닙니다.");
		}

	}

}
